ValidationRule.errorConditionFormula:
AND( RecordType.DeveloperName = 'EstimacionQuote', OR( AND(TEXT(PRIORVALUE(Status)) = 'Aprobada', OR(ISPICKVAL(Status, 'Presupuesto'), ISPICKVAL(Status, 'Draft')) ), AND(TEXT(PRIORVALUE(Status)) = 'Cancelada', OR(ISPICKVAL(Status, 'Presupuesto'), ISPICKVAL(Status, 'Draft')))
)
)
